() => {
    const env_var = pm.environment.toObject({
        excludeDisabled: true
    });
    var body = JSON.parse(pm.collectionVariables.get("userPayload"));
    body.institutionUserId = pm.variables.replaceIn('{{$guid}}');
    body.address.stateProvinceRegion = pm.collectionVariables.get("stateProvinceRegion");
    const createUser = {
        url: env_var.externalLoadBalancer + '/v1/users',
        method: "POST",
        header: {
            "Content-Type": "application/json",
            "x-institution-id": env_var['x-institution-id'],
            "Authorization": pm.request.headers.get("Authorization")
        },
        body: JSON.stringify(body)
    };
    pm.sendRequest(createUser, (error, response) => {
        if (error !== null) {
            console.error('Error while generating a User:', error);
        } else {
            pm.collectionVariables.set("user_institutionUserId", body.institutionUserId);
            pm.collectionVariables.set("user_userId", response.json().id);
            pm.collectionVariables.set("RandomUserBody", body);
        }
    });
}

(userId) => {
    const env_var = pm.environment.toObject({
        excludeDisabled: true
    });
    const getUserRelationship = {
        url: env_var.externalLoadBalancer + '/v1/user-relationships/institution-crypto-user/' + userId,
        method: "GET",
        header: {
            "x-institution-id": env_var['x-institution-id'],
            "Authorization": pm.request.headers.get("Authorization")
        }
    };
    pm.sendRequest(getUserRelationship, (error, response) => {
        if (error !== null) {
            console.error('Error while getting a user relationship:', error);
        } else {
            pm.collectionVariables.set("user_providerUserId", response.json().providerUserId);
            pm.collectionVariables.set("user_providerId", response.json().providerId);
        }
    });
}
